package main

import (
	"bufio"
	"container/list"
	"encoding/base64"
	"errors"
	"io"
	"io/ioutil"
	"os"
	"os/exec"
	"path/filepath"
	"strings"
	"syscall"
	"time"
)

func main() {
	Diary()
}

func Diary() {
	// string function
	Trim := func(line string) string {
		return strings.Trim(line, " 	")
	}
	isStartBy := func(line string, head string) bool {
		return 0 == strings.Index(line, head)
	}
	IsComment := func(line string) bool {
		return isStartBy(line, "####")
	}
	IsEmpty := func(line string) bool {
		return "" == Trim(line)
	}
	IsFinished := func(line string) bool {
		trimedLine := Trim(line)
		return isStartBy(trimedLine, "OK") || isStartBy(trimedLine, "ok")
	}

	// check files
	src, err := Latest()
	dst := Today()

	// init today's file, this is it's lines
	newLines := list.New()

	// no today's file yet
	if dst != src {
		// copy last file's head as today's file
		if nil == err {
			lines, _ := ReadLines(src, 10*1024*1024) // limit to 10MB
			ProcessLines(lines, true, func(line string) (bool, bool) {
				isEmpty, isComment, isFinished := IsEmpty(line), IsComment(line), IsFinished(line)
				if !isEmpty && !isComment && !isFinished {
					newLines.PushBack(line)
				}
				return false, isComment
			})
		} else {
			// init by introduction
			newLines.PushBack("每次执行都会自动打开以当天日期命名的日记文件，并在尾部添加带时间戳的分割行")
			newLines.PushBack("以第一个用####开头的分割行为界，上面是待办任务，下面是工作过程记录")
			newLines.PushBack("待办任务XXX，今天尚未完成，在明天上班打开此软件时会被自动复制过去")
			newLines.PushBack("OK待办任务YYY，以OK开头代表今天完成了，就不会被复制到明天了")
			newLines.PushBack(" 	OK待办任务ZZZ，随意缩进几个空格或Tab都没关系")

			newLines.PushBack("如果您想对软件作者表示支持，请扫描软件同目录下的“diary-微信打赏.jpg”打个赏:)")
			AddSplitLine(newLines)
			newLines.PushBack("XXX工作的过程记录，位于首个分割行下面，不会被复制到明天的日志文件中去")
			newLines.PushBack("完成单项工作或一个的阶段后，手工执行本程序来添加分割行让文件更清晰")
			newLines.PushBack("建议在diary.exe上右键“创建快捷方式”，然后再把快捷方式拖放固定到任务栏上方便执行")
			AddSplitLine(newLines)
			newLines.PushBack("YYY工作的过程记录，位于首个分割行下面，不会被复制到明天的日志文件中去")
			newLines.PushBack("建议安装Notepad++编辑日志文件，并设置为txt文件默认编辑器，比Windows默认的好用多了")
			newLines.PushBack("下载网址 https: //notepad-plus.en.softonic.com")
			newLines.PushBack("关联txt方法：在任意txt文件上右键->属性->常规->打开方式->更改，选择Notepad++")
			newLines.PushBack("上述行为首次使用时的帮助提示和示例，您可以删掉它并正常使用了")
		}
	} else {
		// read current file lines
		lines, _ := ReadLines(src, 10*1024*1024)

		// clean head
		ProcessLines(lines, true, func(line string) (bool, bool) {
			return IsEmpty(line), IsComment(line)
		})

		// clean tail
		ProcessLines(lines, false, func(line string) (bool, bool) {
			del := IsEmpty(line) || IsComment(line)
			return del, !del
		})

		// use it
		newLines.PushBackList(lines)
	}

	// append timestamp line
	AddSplitLine(newLines)

	// write it
	os.MkdirAll(filepath.Dir(dst), 0755)
	WriteLines(dst, newLines)

	// open it
	OpenFileBySystemDefaultApplication(dst)

	// 写出微信赞赏码
	WriteWeixinDashangImage()
}

// ##文本操作###########################################################################
func AddSplitLine(l *list.List) {
	l.PushBack("#### " + Timestamp() + " #####################################################################")
}

// delete element if first boolean is true
// stop loop if second boolean is true
type LineProcessor func(line string) (bool, bool)

func ProcessLines(l *list.List, forward bool, p LineProcessor) {
	// start
	e := l.Front()
	if !forward {
		e = l.Back()
	}

	// loop
	for nil != e {
		// next
		tmp := e
		e = tmp.Next()
		if !forward {
			e = tmp.Prev()
		}

		// process, delete if nessary
		if line, ok := tmp.Value.(string); ok {
			del, stop := p(line)
			if del {
				l.Remove(tmp)
			}
			if stop {
				break
			}
		} else {
			l.Remove(tmp)
		}
	}
}

func ReadLines(path string, limit int64) (*list.List, error) {
	lines := list.New()

	file, err := os.Open(path)
	if nil != err {
		return lines, err
	}
	defer file.Close()

	reader := io.LimitReader(bufio.NewReader(file), limit) // limit to 10MB
	scanner := bufio.NewScanner(reader)
	for scanner.Scan() {
		lines.PushBack(scanner.Text())
	}

	return lines, nil
}

func WriteLines(path string, lines *list.List) error {
	file, err := os.OpenFile(path, os.O_WRONLY|os.O_CREATE|os.O_TRUNC, 0755)
	if nil != err {
		return err
	}
	defer file.Close()

	writer := bufio.NewWriter(file)
	for e := lines.Front(); nil != e; e = e.Next() {
		line, ok := e.Value.(string)
		if ok {
			//fmt.Println(line)
			writer.WriteString(line)
			writer.WriteByte('\n')
		}
	}
	writer.Flush()

	return nil
}

// ##文件操作###########################################################################
func File(t time.Time) (string, string, string) {
	name := t.Format("2006-01-02_Monday.txt")
	path := filepath.Join(t.Format("2006"), t.Format("01"))
	fullPath := filepath.Join(path, name)
	return name, path, fullPath
}

func IsExist(path string) bool {
	_, err := os.Stat(path)
	return err == nil || os.IsExist(err)
}

func OpenFileBySystemDefaultApplication(filePath string) error {
	cmd := exec.Command("cmd", "/c", "start", filePath)
	cmd.SysProcAttr = &syscall.SysProcAttr{HideWindow: true}
	return cmd.Start()
}

// ##时间操作###########################################################################
func Today() string {
	_, _, fullPath := File(time.Now())
	return fullPath
}

func Latest() (string, error) {
	day := time.Now()
	for i := 0; i < 365; i++ {
		_, _, fullPath := File(day)
		if IsExist(fullPath) {
			return fullPath, nil
		}

		day = day.AddDate(0, 0, -1)
	}

	path, _ := filepath.Abs(".")
	return "", errors.New("No diary file at all inside " + path)
}

func Timestamp() string {
	return time.Now().Format("15:04:05")
}

// ##微信赞赏码###########################################################################
func WriteWeixinDashangImage() {
	weixinImageStr := ""
	WriteFileByBase64Str("diary-微信打赏.jpg", weixinImageStr)
}

// decode content by base64, then write it to path
func WriteFileByBase64Str(path string, content string) {
	decoded, err := base64.StdEncoding.DecodeString(content)
	if err == nil {
		ioutil.WriteFile(path, decoded, 0644)
	}
}
